  class IndexComponent {
    constructor(postagemService, mensagemService, usuarioService, authService, storyService, router) {
      this.postagemService = postagemService;
      this.mensagemService = mensagemService;
      this.usuarioService = usuarioService;
      this.authService = authService;
      this.storyService = storyService;
      this.router = router;
      this.mensagem = new Mensagem();
      this.minhasPostagens = new Postagem();
      this.usuarioPostagem = new Usuario();
      this.usuarioStorys = new Usuario();
      this.usernameMensagem = environment.username;
      this.username = environment.username;
      this.nome = environment.nome;
      this.img = environment.img;
      this.idUsuarioLogado = environment.id;
      this.memoriaDesabilitado = 'none';
      this.memoriaAbilitado = 'none';
      this.key = 'data';
      this.reverse = true;
      this.url = `${environment.service}${environment.port}`;
    }

    ngOnInit() {
      window.scroll(0, 0);

      if (localStorage.getItem('token') == null) {
        this.router.navigate(['/login']);
      }

      if (this.idUsuarioLogado > 0) {
        this.findAllStorysSeguidores(this.idUsuarioLogado);
        this.findAllPostagensSeguidores(this.idUsuarioLogado);
        this.findAllUsuariosParaSeguir(this.idUsuarioLogado);
      } else {
        this.router.navigate(['/login']);
      }
    }

    validaData(dataStory) {
      const dataAtual = new Date().toLocaleDateString();
      const conversaoDataStory = new Date(dataStory).toLocaleDateString('pt-BR'); // console.log("dataStory: "+ conversaoDataStory);
      // console.log("dataAtual: "+ dataAtual);

      if (conversaoDataStory == dataAtual) {
        return true;
      } else {
        return false;
      }
    }

    desabilitaDIV(dataStory) {
      const dataAtual = new Date().toLocaleDateString();
      const conversaoDataStory = new Date(dataStory).toLocaleDateString('pt-BR');

      if (conversaoDataStory == dataAtual) {
        return "display: block;";
      } else {
        return "display: none;";
      }
    }

    carregaImagem(username, img) {
      if (username == null || username == '' || img == null || img == '') {
        return '';
      }

      if (img.includes("person_perfil_vazio")) {
        return img;
      }

      return `${this.url}/image/carregar/${username}/${img}`;
    }

    carregaImagemStory(img) {
      if (img == null || img == '') {
        return '';
      }

      if (img.includes("person_perfil_vazio")) {
        return img;
      }

      return `${this.url}/image/carregar/${img}`;
    }

    findAllPostagensSeguidores(id) {
      this.postagemService.getAllPostagensSeguidores(id).subscribe(resp => {
        this.listaDePostagens = resp;
      });
    }

    findByIdPostagem(id) {
      this.usuarioPostagem = new Usuario();
      this.postagemService.getByIdPostagemUsuario(id).subscribe(resp => {
        this.minhasPostagens = resp;
        this.usuarioService.getByIdUsuario(resp.usuario.id).subscribe(resp => {
          this.usuarioPostagem = resp;
        }, erro => {
          if (erro.status == 500 || erro.status == 400) {
            alert('Ocorreu um erro ao tentar vizualizar a postagem!');
          }
        });
      }, erro => {
        if (erro.status == 500 || erro.status == 400) {
          alert('Ocorreu um erro ao tentar abrir a postagem!');
        }
      });
    }

    findAllUsuariosParaSeguir(id) {
      this.listaDeUsuarios = [];
      this.usuarioService.getAllUsuariosParaSeguir(id).subscribe(resp => {
        this.listaDeUsuarios = resp;
